INSERT INTO `t_career` VALUES (4,'Analis Laboratorium','','RSUD Cimacan membuka peluang dan kesempatan bergabung bagi:<br />Analis Laboratorium<br />(Jumlah: 4 orang)<br /><br />Kriteria:<br />- Pria/Wanita<br />- Usia maksimal 35 tahun<br />- Kemampuan komunikasi yang baik<br />- Jujur, cekatan, bertanggung jawab<br />- Dapat bekerja sama dalam tim<br />- Menguasai Microsoft Office (Word, Excel, PPT)<br />- Bersedia bekerja dalam shift<br /><br />Yuk kirimkan surat lamaran, CV, foto terbaru, dan kelengkapan<br />melalui e-mail ke : umpeg.rsudcimacan@gmail.com','career/20220105140612_analis_laboratorium_Desember.jpg','','unpublish','2021-12-20 14:06:12','2022-01-05 14:06:12'),(6,'Tenaga Kesehatan','','RSUD Cimacan membuka peluang dan kesempatan bergabung bagi:<br />1. Apoteker<br />2. Tenaga Teknis Kefarmasian (TTK)<br />3. Asisten TTK<br /><br />Kriteria:<br />- Pria/Wanita<br />- Kemampuan komunikasi yang baik<br />- Jujur, cekatan, bertanggung jawab<br />- Dapat bekerja sama dalam tim<br />- Menguasai Microsoft Office (Word, Excel, PPT)<br />- Bersedia bekerja dalam shift<br /><br />Kirimkan surat lamaran, CV, foto terbaru, dan kelengkapan<br />melalui e-mail ke : umpeg.rsudcimacan@gmail.com','career/20220106090336_recruitment_opteker_ttk_asttk.jpg','','unpublish','2021-12-04 09:03:36','2022-01-06 09:03:36'),(7,'Perawat','RSD Cimacan membuka peluang dan kesempatan bergabung bagi:\r\nPerawat\r\n(Jumlah: 33 orang)\r\n\r\nKriteria:\r\n- Pendidikan minimal D3 Keperawatan\r\n- Diutamakan program Profesi Ners\r\n- Memiliki STR/Serk','RSD Cimacan membuka peluang dan kesempatan bergabung bagi:<br />Perawat<br />(Jumlah: 33 orang)<br /><br />Kriteria:<br />- Pendidikan minimal D3 Keperawatan<br />- Diutamakan program Profesi Ners<br />- Memiliki STR/Serkom<br />- Diutamakan mempunyaj Sertifikat Pelatihan Khusus (Bedah, ICU, HD, dll)<br />- Kemampuan komunikasi yang baik<br />- Jujur, cekatan, bertanggung jawab<br />- Dapat bekerja sama dalam tim<br />- Bersedia bekerja dalam shift<br /><br />Periode pendaftaran dibuka dari tanggal 14 hingga 20 Januari 2022.<br />Yuk segera kirimkan surat lamaran, CV, foto terbaru, dan kelengkapan<br />melalui e-mail ke: umpeg.rsudcimacan@gmail.com','career/20220117091353_271904694_910159676368644_5753862463631661069_n.jpg','','unpublish','2022-01-15 09:13:53','2022-01-17 09:13:53'),(8,'Dokter Umum','','RSD Cimacan membuka peluang dan kesempatan bergabung bagi:<br />Dokter Umum<br />(Jumlah: 3 orang)<br /><br />Kriteria:<br />- Pendidikan Profesi Dokter<br />- Memiliki STR aktif<br />- Memiliki sertifikat ACLS/ATLS<br />- Kemampuan komunikasi yang baik<br />- Jujur, cekatan, bertanggung jawab<br />- Dapat bekerja sama dalam tim<br />- Bersedia bekerja dalam shift<br />- Memiliki sertifikat pelatihan, seminar/workshop yang mendukung profesi<br /><br />Yuk segera kirimkan surat lamaran, CV, foto terbaru, dan kelengkapan melalui e-mail ke: umpeg.rsudcimacan@gmail.com','career/20220119091738_Lowongan-Dokter-Umum.jpg','','unpublish','2022-01-19 09:17:38','2022-01-19 09:17:38'),(9,'Admin Ruangan','','RSD Cimacan membuka peluang dan kesempatan bergabung bagi:<br />Admin Ruangan<br />(Jumlah: 1 orang)<br /><br />Kriteria:<br />- Lulusan SMA/SMK<br />- Handal mengoperasikan sistem komputer<br />- Kemampuan komunikasi yang baik<br />- Jujur, cekatan, bertanggung jawab<br />- Dapat bekerja sama dalam tim<br />- Bersedia bekerja dalam shift<br /><br />Yuk segera kirimkan surat lamaran, CV, foto terbaru, dan kelengkapan<br />melalui e-mail ke: umpeg.rsudcimacan@gmail.com','career/20220121112411_lowongan-admin-ruangan.jpg','','unpublish','2022-01-21 11:24:12','2022-01-21 11:24:12'),(10,'Dokter Umum','','RSUD Cimacan membuka peluang dan kesempatan bergabung bagi:<br />Dokter Umum<br />(Jumlah: 3 orang)<br /><br />Kriteria:<br />1.Pendidikan Profesi Dokter<br />2.Memiliki STR aktif<br />3.Memiliki sertifikat ACLS dan ATLS<br />4. Surat keterangan kerja dari tempat bekerja sebelumnya<br />5. Kemampuan komunikasi yang